func main() {
	// Load configuration
	cfg := config.NewServiceConfig("notification", 8084)
	kafkaCfg := config.NewKafkaConfig("notification-service")

	// Database config
	dbHost := config.GetEnv("DB_HOST", "localhost")
	dbPort := config.GetEnv("DB_PORT", "5435")
	dbUser := config.GetEnv("DB_USER", "rinder")
	dbPassword := config.GetEnv("DB_PASSWORD", "rinder123")
	dbName := config.GetEnv("DB_NAME", "rinder_notifications")

	// User service URL (for getting user names)
	userServiceURL := config.GetEnv("USER_SERVICE_URL", "http://localhost:8081")

	// Initialize logger
	logger.Init(cfg.Name, cfg.LogLevel)
	logger.Info("starting notification service",
		zap.Int("port", cfg.Port),
		zap.String("environment", cfg.Environment),
	)

	// Connect to PostgreSQL
	dsn := fmt.Sprintf("host=%s port=%s user=%s password=%s dbname=%s sslmode=disable",
		dbHost, dbPort, dbUser, dbPassword, dbName)

	db, err := sql.Open("postgres", dsn)
	if err != nil {
		logger.Fatal("failed to connect to database", zap.Error(err))
	}
	defer db.Close()

	// Verify connection
	if err := db.Ping(); err != nil {
		logger.Fatal("failed to ping database", zap.Error(err))
	}
	logger.Info("connected to PostgreSQL")

	// Run migrations
	if err := runMigrations(db); err != nil {
		logger.Fatal("failed to run migrations", zap.Error(err))
	}
	logger.Info("database migrations complete")

	// Initialize repository and service
	notificationRepo := repository.NewNotificationRepository(db)
	notificationService := service.NewNotificationService(notificationRepo)

	// Initialize Kafka consumer
	matchConsumer := consumer.NewMatchConsumer(kafkaCfg.Brokers, notificationService, userServiceURL)

	// Start consumer
	ctx, cancel := context.WithCancel(context.Background())
	matchConsumer.Start(ctx)

	// Initialize handler
	h := handler.NewHandler(notificationService)

	// Setup routes
	mux := http.NewServeMux()
	h.RegisterRoutes(mux)

	// Apply middleware
	var httpHandler http.Handler = mux
	httpHandler = middleware.Logging(httpHandler)
	httpHandler = middleware.Recovery(httpHandler)

	// Create server
	server := &http.Server{
		Addr:         fmt.Sprintf(":%d", cfg.Port),
		Handler:      httpHandler,
		ReadTimeout:  15 * time.Second,
		WriteTimeout: 15 * time.Second,
		IdleTimeout:  60 * time.Second,
	}

	// Start server in goroutine
	go func() {
		logger.Info("notification service listening", zap.String("addr", server.Addr))
		if err := server.ListenAndServe(); err != nil && err != http.ErrServerClosed {
			logger.Fatal("failed to start server", zap.Error(err))
		}
	}()

	// Wait for interrupt
	quit := make(chan os.Signal, 1)
	signal.Notify(quit, syscall.SIGINT, syscall.SIGTERM)
	<-quit

	logger.Info("shutting down server...")

	// Stop consumer
	cancel()
	matchConsumer.Stop()

	// Graceful shutdown
	shutdownCtx, shutdownCancel := context.WithTimeout(context.Background(), 30*time.Second)
	defer shutdownCancel()

	if err := server.Shutdown(shutdownCtx); err != nil {
		logger.Fatal("server forced to shutdown", zap.Error(err))
	}

	logger.Info("server stopped")
}

