func NewMatchConsumer(
	brokers []string,
	notificationService *service.NotificationService,
	userServiceURL string,
) *MatchConsumer {
	consumer := kafka.NewConsumer(brokers, "notification-service", []string{kafka.TopicMatchCreated})

	mc := &MatchConsumer{
		consumer:            consumer,
		notificationService: notificationService,
		userClient:          httpclient.NewClient(userServiceURL, "user-service", 10*time.Second),
	}

	// Register handler for match created events
	consumer.RegisterHandler(kafka.EventMatchCreated, mc.handleMatchCreated)

	return mc
}

func (c *MatchConsumer) handleMatchCreated(ctx context.Context, event kafka.Event) error {
	var matchData struct {
		MatchID    string `json:"match_id"`
		User1ID    string `json:"user1_id"`
		User2ID    string `json:"user2_id"`
		Profile1ID string `json:"profile1_id"`
		Profile2ID string `json:"profile2_id"`
		Timestamp  string `json:"timestamp"`
	}

	if err := json.Unmarshal(event.Data, &matchData); err != nil {
		logger.Error("failed to unmarshal match data", zap.Error(err))
		return nil // Don't retry malformed messages
	}

	logger.Info("received match event",
		zap.String("match_id", matchData.MatchID),
		zap.String("user1_id", matchData.User1ID),
		zap.String("user2_id", matchData.User2ID),
	)

	// Get user names for personalized notifications
	user1Name := c.getUserName(ctx, matchData.User1ID)
	user2Name := c.getUserName(ctx, matchData.User2ID)

	// Send notification to User1
	_, err := c.notificationService.CreateMatchNotification(matchData.User1ID, matchData.MatchID, user2Name)
	if err != nil {
		logger.Error("failed to create notification for user1", zap.String("user_id", matchData.User1ID), zap.Error(err))
	}

	// Send notification to User2
	_, err = c.notificationService.CreateMatchNotification(matchData.User2ID, matchData.MatchID, user1Name)
	if err != nil {
		logger.Error("failed to create notification for user2", zap.String("user_id", matchData.User2ID), zap.Error(err))
	}

	return nil
}

func (c *MatchConsumer) getUserName(ctx context.Context, userID string) string {
	resp, err := c.userClient.Get(ctx, fmt.Sprintf("/api/v1/users/%s", userID), nil)
	if err != nil {
		logger.Warn("failed to get user name", zap.String("user_id", userID), zap.Error(err))
		return "Someone"
	}

	var result struct {
		Success bool `json:"success"`
		Data    struct {
			FirstName string `json:"first_name"`
		} `json:"data"`
	}
	if err := json.Unmarshal(resp.Body, &result); err != nil || !result.Success {
		return "Someone"
	}

	if result.Data.FirstName != "" {
		return result.Data.FirstName
	}
	return "Someone"
}

