func (s *NotificationService) CreateNotification(req *model.CreateNotificationRequest) (*model.Notification, error) {
	// Get user preferences
	prefs, err := s.repo.GetPreferences(req.UserID)
	if err != nil {
		logger.Warn("failed to get preferences", zap.String("user_id", req.UserID), zap.Error(err))
		// Continue with defaults
		prefs = &model.NotificationPreference{
			PushEnabled:   true,
			MatchNotify:   true,
			MessageNotify: true,
			LikeNotify:    true,
		}
	}

	// Check if user wants this type of notification
	switch req.Type {
	case model.NotificationTypeMatch:
		if !prefs.MatchNotify {
			logger.Info("user has disabled match notifications", zap.String("user_id", req.UserID))
			return nil, nil
		}
	case model.NotificationTypeMessage:
		if !prefs.MessageNotify {
			logger.Info("user has disabled message notifications", zap.String("user_id", req.UserID))
			return nil, nil
		}
	case model.NotificationTypeLike:
		if !prefs.LikeNotify {
			logger.Info("user has disabled like notifications", zap.String("user_id", req.UserID))
			return nil, nil
		}
	}

	// Create notification
	notification := &model.Notification{
		UserID: req.UserID,
		Type:   req.Type,
		Title:  req.Title,
		Body:   req.Body,
		Data:   req.Data,
	}

	if err := s.repo.Create(notification); err != nil {
		return nil, fmt.Errorf("failed to create notification: %w", err)
	}

	// Send push notification if enabled
	if prefs.PushEnabled {
		if err := s.sendPushNotification(notification); err != nil {
			logger.Warn("failed to send push notification", zap.Error(err))
			// Don't fail the request, just log
		}
	}

	// Mark as sent
	s.repo.UpdateStatus(notification.ID, model.NotificationStatusSent)
	notification.Status = model.NotificationStatusSent

	return notification, nil
}

func (r *NotificationRepository) GetPreferences(userID string) (*model.NotificationPreference, error) {
	query := `
		SELECT user_id, push_enabled, email_enabled, match_notify, message_notify, like_notify, created_at, updated_at
		FROM notification_preferences
		WHERE user_id = $1
	`

	pref := &model.NotificationPreference{}
	err := r.db.QueryRow(query, userID).Scan(
		&pref.UserID,
		&pref.PushEnabled,
		&pref.EmailEnabled,
		&pref.MatchNotify,
		&pref.MessageNotify,
		&pref.LikeNotify,
		&pref.CreatedAt,
		&pref.UpdatedAt,
	)
	if err == sql.ErrNoRows {
		// Return default preferences
		return &model.NotificationPreference{
			UserID:        userID,
			PushEnabled:   true,
			EmailEnabled:  true,
			MatchNotify:   true,
			MessageNotify: true,
			LikeNotify:    true,
		}, nil
	}
	if err != nil {
		return nil, fmt.Errorf("failed to get preferences: %w", err)
	}

	return pref, nil
}

func (s *NotificationService) sendPushNotification(notification *model.Notification) error {
	// Get user's push tokens
	tokens, err := s.repo.GetPushTokens(notification.UserID)
	if err != nil {
		return fmt.Errorf("failed to get push tokens: %w", err)
	}

	if len(tokens) == 0 {
		logger.Debug("no push tokens for user", zap.String("user_id", notification.UserID))
		return nil
	}

	// In a real implementation, this would send to FCM/APNS
	// For now, just log
	for _, token := range tokens {
		logger.Info("would send push notification",
			zap.String("user_id", notification.UserID),
			zap.String("platform", token.Platform),
			zap.String("title", notification.Title),
		)
	}

	return nil
}

func (r *NotificationRepository) GetPushTokens(userID string) ([]*model.PushToken, error) {
	query := `
		SELECT id, user_id, token, platform, created_at, updated_at
		FROM push_tokens
		WHERE user_id = $1
	`

	rows, err := r.db.Query(query, userID)
	if err != nil {
		return nil, fmt.Errorf("failed to get push tokens: %w", err)
	}
	defer rows.Close()

	var tokens []*model.PushToken
	for rows.Next() {
		token := &model.PushToken{}
		err := rows.Scan(
			&token.ID,
			&token.UserID,
			&token.Token,
			&token.Platform,
			&token.CreatedAt,
			&token.UpdatedAt,
		)
		if err != nil {
			return nil, fmt.Errorf("failed to scan push token: %w", err)
		}
		tokens = append(tokens, token)
	}

	return tokens, nil
}

