func (h *Handler) handleNotifications(w http.ResponseWriter, r *http.Request) {
	// Exact path match only
	if r.URL.Path != "/api/v1/notifications" {
		response.Error(w, errors.NotFound("Not found"))
		return
	}

	switch r.Method {
	case http.MethodGet:
		h.getNotifications(w, r)
