		h.createNotification(w, r)
	default:
		response.Error(w, errors.BadRequest("Method not allowed"))
	}
}

func (h *Handler) getNotifications(w http.ResponseWriter, r *http.Request) {
	userID := r.Header.Get("X-User-ID")
	if userID == "" {
		response.Error(w, errors.Unauthorized("User not authenticated"))
		return
	}

	// Parse query params
	limitStr := r.URL.Query().Get("limit")
	offsetStr := r.URL.Query().Get("offset")

	limit := 20
	offset := 0

	if limitStr != "" {
		if l, err := strconv.Atoi(limitStr); err == nil {
			limit = l
		}
	}
	if offsetStr != "" {
		if o, err := strconv.Atoi(offsetStr); err == nil {
			offset = o
		}
	}

	notifications, err := h.notificationService.GetNotifications(userID, limit, offset)
	if err != nil {
		response.Error(w, errors.InternalServerError("Failed to get notifications"))
		return
	}

	if notifications == nil {
		notifications = []*model.Notification{}
	}

	response.OK(w, notifications)
}

func (s *NotificationService) GetNotifications(userID string, limit, offset int) ([]*model.Notification, error) {
	if limit <= 0 || limit > 100 {
		limit = 20
	}
	if offset < 0 {
		offset = 0
	}

	notifications, err := s.repo.GetByUserID(userID, limit, offset)
	if err != nil {
		return nil, fmt.Errorf("failed to get notifications: %w", err)
	}

	return notifications, nil
}

func (r *NotificationRepository) GetByUserID(userID string, limit, offset int) ([]*model.Notification, error) {
	query := `
		SELECT id, user_id, type, title, body, data, status, read_at, created_at, updated_at
		FROM notifications
		WHERE user_id = $1
		ORDER BY created_at DESC
		LIMIT $2 OFFSET $3
	`

	rows, err := r.db.Query(query, userID, limit, offset)
	if err != nil {
		return nil, fmt.Errorf("failed to get notifications: %w", err)
	}
	defer rows.Close()

	var notifications []*model.Notification
	for rows.Next() {
		notification := &model.Notification{}
		var dataJSON []byte
		var readAt sql.NullTime

		err := rows.Scan(
			&notification.ID,
			&notification.UserID,
			&notification.Type,
			&notification.Title,
			&notification.Body,
			&dataJSON,
			&notification.Status,
			&readAt,
			&notification.CreatedAt,
			&notification.UpdatedAt,
		)
		if err != nil {
			return nil, fmt.Errorf("failed to scan notification: %w", err)
		}

		if readAt.Valid {
			notification.ReadAt = &readAt.Time
		}
		json.Unmarshal(dataJSON, &notification.Data)
		notifications = append(notifications, notification)
	}

	return notifications, nil
}

