func (h *Handler) handleNotificationByID(w http.ResponseWriter, r *http.Request) {
	// Extract notification ID from path
	path := strings.TrimPrefix(r.URL.Path, "/api/v1/notifications/")
	if path == "" || strings.Contains(path, "/") {
		response.Error(w, errors.NotFound("Notification not found"))
		return
	}
	notificationID := path

	userID := r.Header.Get("X-User-ID")
	if userID == "" {
		response.Error(w, errors.Unauthorized("User not authenticated"))
		return
	}

	switch r.Method {
	case http.MethodPatch:
		h.markAsRead(w, r, userID, notificationID)
	default:
		response.Error(w, errors.BadRequest("Method not allowed"))
	}
}

func (h *Handler) markAsRead(w http.ResponseWriter, r *http.Request, userID, notificationID string) {
	err := h.notificationService.MarkAsRead(userID, notificationID)
	if err != nil {
		if strings.Contains(err.Error(), "not found") {
			response.Error(w, errors.NotFound("Notification not found"))
			return
		}
		if strings.Contains(err.Error(), "not authorized") {
			response.Error(w, errors.Forbidden("Not authorized"))
			return
		}
		response.Error(w, errors.InternalServerError("Failed to mark as read"))
		return
	}

	response.OK(w, map[string]string{"message": "Marked as read"})
}

func (s *NotificationService) MarkAsRead(userID, notificationID string) error {
	// Verify ownership
	notification, err := s.repo.GetByID(notificationID)
	if err != nil {
		return fmt.Errorf("failed to get notification: %w", err)
	}
	if notification == nil {
		return fmt.Errorf("notification not found")
	}
	if notification.UserID != userID {
		return fmt.Errorf("not authorized")
	}

	if err := s.repo.MarkAsRead(notificationID); err != nil {
		return fmt.Errorf("failed to mark as read: %w", err)
	}

	return nil
}

func (r *NotificationRepository) GetByID(id string) (*model.Notification, error) {
