func (h *Handler) handlePreferences(w http.ResponseWriter, r *http.Request) {
	userID := r.Header.Get("X-User-ID")
	if userID == "" {
		response.Error(w, errors.Unauthorized("User not authenticated"))
		return
	}

	switch r.Method {
	case http.MethodGet:
		prefs, err := h.notificationService.GetPreferences(userID)
		if err != nil {
			response.Error(w, errors.InternalServerError("Failed to get preferences"))
			return
		}
		response.OK(w, prefs)

	case http.MethodPut:
		var req model.UpdatePreferencesRequest
		if err := json.NewDecoder(r.Body).Decode(&req); err != nil {
			response.Error(w, errors.BadRequest("Invalid request body"))
			return
		}

		prefs, err := h.notificationService.UpdatePreferences(userID, &req)
		if err != nil {
			response.Error(w, errors.InternalServerError("Failed to update preferences"))
			return
		}
		response.OK(w, prefs)

	default:
		response.Error(w, errors.BadRequest("Method not allowed"))
	}
}

