	if err != nil {
		return nil, fmt.Errorf("failed to get preferences: %w", err)
	}

	// Update fields if provided
	if req.PushEnabled != nil {
		prefs.PushEnabled = *req.PushEnabled
	}
	if req.EmailEnabled != nil {
		prefs.EmailEnabled = *req.EmailEnabled
	}
	if req.MatchNotify != nil {
		prefs.MatchNotify = *req.MatchNotify
	}
	if req.MessageNotify != nil {
		prefs.MessageNotify = *req.MessageNotify
	}
	if req.LikeNotify != nil {
		prefs.LikeNotify = *req.LikeNotify
	}

	// Save
	if err := s.repo.SavePreferences(prefs); err != nil {
		return nil, fmt.Errorf("failed to save preferences: %w", err)
	}

	return prefs, nil
}

func (h *Handler) handlePushToken(w http.ResponseWriter, r *http.Request) {
	userID := r.Header.Get("X-User-ID")
	if userID == "" {
		response.Error(w, errors.Unauthorized("User not authenticated"))
		return
	}

	switch r.Method {
	case http.MethodPost:
		var req model.RegisterTokenRequest
		if err := json.NewDecoder(r.Body).Decode(&req); err != nil {
			response.Error(w, errors.BadRequest("Invalid request body"))
			return
		}

		err := h.notificationService.RegisterPushToken(userID, &req)
		if err != nil {
			response.Error(w, errors.InternalServerError("Failed to register push token"))
			return
		}
		response.OK(w, map[string]string{"message": "Push token registered"})

	case http.MethodDelete:
		platform := r.URL.Query().Get("platform")
		if platform == "" {
			response.Error(w, errors.BadRequest("Platform is required"))
			return
		}

		err := h.notificationService.UnregisterPushToken(userID, platform)
		if err != nil {
			response.Error(w, errors.InternalServerError("Failed to delete push token"))
			return
		}
		response.OK(w, map[string]string{"message": "Push token deleted"})

	default:
		response.Error(w, errors.BadRequest("Method not allowed"))
	}
}

func (s *NotificationService) RegisterPushToken(userID string, req *model.RegisterTokenRequest) error {
	if req.Token == "" {
		return fmt.Errorf("token is required")
	}
	if req.Platform == "" {
		return fmt.Errorf("platform is required")
	}

	if err := s.repo.SavePushToken(userID, req.Token, req.Platform); err != nil {
		return fmt.Errorf("failed to save push token: %w", err)
	}

	return nil
}

func (r *NotificationRepository) SavePushToken(userID, token, platform string) error {
	query := `
		INSERT INTO push_tokens (id, user_id, token, platform, created_at, updated_at)
		VALUES ($1, $2, $3, $4, $5, $6)
		ON CONFLICT (user_id, platform) DO UPDATE SET
			token = $3,
			updated_at = $6
	`

	now := time.Now()
	_, err := r.db.Exec(query, uuid.New().String(), userID, token, platform, now, now)
	if err != nil {
		return fmt.Errorf("failed to save push token: %w", err)
	}

	return nil
}

func (s *NotificationService) UnregisterPushToken(userID, platform string) error {
	if err := s.repo.DeletePushToken(userID, platform); err != nil {
		return fmt.Errorf("failed to delete push token: %w", err)
	}
	return nil
}

func (r *NotificationRepository) DeletePushToken(userID, platform string) error {
	query := `DELETE FROM push_tokens WHERE user_id = $1 AND platform = $2`
	_, err := r.db.Exec(query, userID, platform)
	if err != nil {
		return fmt.Errorf("failed to delete push token: %w", err)
	}
	return nil
}

func (c *MatchConsumer) Stop() {
	if err := c.consumer.Close(); err != nil {
		logger.Error("failed to close consumer", zap.Error(err))
	}
}
