func main() {
	// Load configuration
	cfg := config.NewServiceConfig("payment-gateway", 8091)
	kafkaCfg := config.NewKafkaConfig("payment-gateway-service")

	// Payment provider URLs
	stripeURL := config.GetEnv("PAYMENT_STRIPE_URL", "http://localhost:8092")
	braintreeURL := config.GetEnv("PAYMENT_BRAINTREE_URL", "http://localhost:8093")
	worldpayURL := config.GetEnv("PAYMENT_WORLDPAY_URL", "http://localhost:8094")

	// Initialize logger
	logger.Init(cfg.Name, cfg.LogLevel)
	logger.Info("starting payment gateway service",
		zap.Int("port", cfg.Port),
		zap.String("environment", cfg.Environment),
		zap.String("stripe_url", stripeURL),
		zap.String("braintree_url", braintreeURL),
		zap.String("worldpay_url", worldpayURL),
	)

	// Initialize Kafka producer
	producer := kafka.NewProducer(kafkaCfg.Brokers, cfg.Name)
	defer producer.Close()

	// Initialize service
	gatewayService := service.NewGatewayService(producer, stripeURL, braintreeURL, worldpayURL)

	// Initialize handler
	h := handler.NewHandler(gatewayService)

	// Setup routes
	mux := http.NewServeMux()
	h.RegisterRoutes(mux)

	// Apply middleware
	var httpHandler http.Handler = mux
	httpHandler = middleware.Logging(httpHandler)
	httpHandler = middleware.Recovery(httpHandler)

	// Create server
	server := &http.Server{
		Addr:         fmt.Sprintf(":%d", cfg.Port),
		Handler:      httpHandler,
		ReadTimeout:  30 * time.Second,
		WriteTimeout: 30 * time.Second,
		IdleTimeout:  60 * time.Second,
	}

	// Start server
	go func() {
		logger.Info("payment gateway service listening", zap.String("addr", server.Addr))
		if err := server.ListenAndServe(); err != nil && err != http.ErrServerClosed {
			logger.Fatal("failed to start server", zap.Error(err))
		}
	}()

	// Wait for interrupt signal
	quit := make(chan os.Signal, 1)
	signal.Notify(quit, syscall.SIGINT, syscall.SIGTERM)
	<-quit

	logger.Info("shutting down server...")

	// Graceful shutdown
	ctx, cancel := context.WithTimeout(context.Background(), 30*time.Second)
	defer cancel()

	if err := server.Shutdown(ctx); err != nil {
		logger.Fatal("server forced to shutdown", zap.Error(err))
	}

	logger.Info("server exited properly")
}

func NewGatewayService(
	producer *kafka.Producer,
	stripeURL string,
	braintreeURL string,
	worldpayURL string,
) *GatewayService {
	providerURLs := map[string]string{
		model.ProviderStripe:    stripeURL,
		model.ProviderBraintree: braintreeURL,
		model.ProviderWorldpay:  worldpayURL,
	}

	providerClients := make(map[string]*httpclient.Client)
	for provider, url := range providerURLs {
		providerClients[provider] = httpclient.NewClient(url, fmt.Sprintf("payment-%s", provider), 30*time.Second)
	}

	return &GatewayService{
		producer:        producer,
		providerClients: providerClients,
		providerURLs:    providerURLs,
	}
}

func (h *Handler) RegisterRoutes(mux *http.ServeMux) {
	// Health check
	mux.HandleFunc("/health", h.healthCheck)

	// Payment routes
	mux.HandleFunc("/api/v1/payments/charge", h.charge)
	mux.HandleFunc("/api/v1/payments/refund", h.refund)

	// Payment method routes
	mux.HandleFunc("/api/v1/payment-methods", h.paymentMethods)

	// Provider routes
	mux.HandleFunc("/api/v1/providers", h.listProviders)
	mux.HandleFunc("/api/v1/providers/health", h.providerHealth)
}

func (h *Handler) healthCheck(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "application/json")
	w.WriteHeader(http.StatusOK)
	w.Write([]byte(`{"status":"healthy","service":"payment-gateway"}`))
}

func (h *Handler) charge(w http.ResponseWriter, r *http.Request) {
	if r.Method != http.MethodPost {
		response.Error(w, &methodNotAllowedError{})
		return
	}

	var req model.ChargeRequest
	if err := json.NewDecoder(r.Body).Decode(&req); err != nil {
		response.Error(w, &badRequestError{message: "invalid request body"})
		return
	}

	// Use default provider if not specified
	if req.Provider == "" {
		req.Provider = model.ProviderStripe
	}

	// Set default currency
	if req.Currency == "" {
		req.Currency = "USD"
	}

	result, err := h.gatewayService.Charge(r.Context(), &req)
	if err != nil {
		response.Error(w, err)
		return
	}

	response.OK(w, result)
}

func (s *GatewayService) Charge(ctx context.Context, req *model.ChargeRequest) (*model.ChargeResponse, error) {
	// Validate provider
	client, ok := s.providerClients[req.Provider]
	if !ok {
		return nil, errors.ValidationError(fmt.Sprintf("unsupported payment provider: %s", req.Provider))
	}

	// Validate amount
	if req.Amount <= 0 {
		return nil, errors.ValidationError("amount must be greater than zero")
	}

	logger.Info("processing charge",
		zap.String("provider", req.Provider),
		zap.Float64("amount", req.Amount),
		zap.String("currency", req.Currency),
		zap.String("user_id", req.UserID),
	)

	// Forward request to provider
	resp, err := client.Post(ctx, "/api/v1/charge", req, nil)
	if err != nil {
		logger.Error("failed to call payment provider",
			zap.String("provider", req.Provider),
			zap.Error(err),
		)
		return &model.ChargeResponse{
			Success:      false,
			Status:       model.PaymentStatusFailed,
			ErrorMessage: "payment provider unavailable",
		}, nil
	}

	var chargeResp model.ChargeResponse
	if err := resp.DecodeJSON(&chargeResp); err != nil {
		logger.Error("failed to decode provider response",
			zap.String("provider", req.Provider),
			zap.Error(err),
		)
		return &model.ChargeResponse{
			Success:      false,
			Status:       model.PaymentStatusFailed,
			ErrorMessage: "invalid provider response",
		}, nil
	}

	// Publish payment event
	if chargeResp.Success {
		s.publishPaymentEvent(ctx, kafka.TopicPaymentSucceeded, kafka.EventPaymentSucceeded, req, &chargeResp)
	} else {
		s.publishPaymentEvent(ctx, kafka.TopicPaymentFailed, kafka.EventPaymentFailed, req, &chargeResp)
	}

	return &chargeResp, nil
}

func (s *GatewayService) publishPaymentEvent(ctx context.Context, topic, eventType string, req *model.ChargeRequest, resp *model.ChargeResponse) {
	event := map[string]interface{}{
		"transaction_id":  resp.TransactionID,
		"user_id":         req.UserID,
		"subscription_id": req.SubscriptionID,
		"amount":          req.Amount,
		"currency":        req.Currency,
		"provider":        req.Provider,
		"status":          resp.Status,
		"error_message":   resp.ErrorMessage,
		"timestamp":       time.Now(),
	}

	key := resp.TransactionID
	if key == "" {
		key = uuid.New().String()
	}

	if err := s.producer.PublishWithKey(ctx, topic, key, eventType, event); err != nil {
		logger.Error("failed to publish payment event",
			zap.String("topic", topic),
			zap.Error(err),
		)
	}
}

func (h *Handler) refund(w http.ResponseWriter, r *http.Request) {
	if r.Method != http.MethodPost {
		response.Error(w, &methodNotAllowedError{})
		return
	}

	var req model.RefundRequest
	if err := json.NewDecoder(r.Body).Decode(&req); err != nil {
		response.Error(w, &badRequestError{message: "invalid request body"})
		return
	}

	if req.Provider == "" {
		response.Error(w, &badRequestError{message: "provider is required"})
		return
	}

	result, err := h.gatewayService.Refund(r.Context(), &req)
	if err != nil {
		response.Error(w, err)
		return
	}

	response.OK(w, result)
}

func (s *GatewayService) Refund(ctx context.Context, req *model.RefundRequest) (*model.RefundResponse, error) {
	// Validate provider
	client, ok := s.providerClients[req.Provider]
	if !ok {
		return nil, errors.ValidationError(fmt.Sprintf("unsupported payment provider: %s", req.Provider))
	}

	logger.Info("processing refund",
		zap.String("provider", req.Provider),
		zap.String("transaction_id", req.TransactionID),
		zap.Float64("amount", req.Amount),
	)

	// Forward request to provider
	resp, err := client.Post(ctx, "/api/v1/refund", req, nil)
	if err != nil {
		logger.Error("failed to call payment provider for refund",
			zap.String("provider", req.Provider),
			zap.Error(err),
		)
		return &model.RefundResponse{
			Success:      false,
			Status:       model.PaymentStatusFailed,
			ErrorMessage: "payment provider unavailable",
		}, nil
	}

	var refundResp model.RefundResponse
	if err := resp.DecodeJSON(&refundResp); err != nil {
		return &model.RefundResponse{
			Success:      false,
			Status:       model.PaymentStatusFailed,
			ErrorMessage: "invalid provider response",
		}, nil
	}

	// Publish refund event if successful
	if refundResp.Success {
		s.publishRefundEvent(ctx, req, &refundResp)
	}

	return &refundResp, nil
}

func (s *GatewayService) publishRefundEvent(ctx context.Context, req *model.RefundRequest, resp *model.RefundResponse) {
	event := map[string]interface{}{
		"refund_id":      resp.RefundID,
		"transaction_id": req.TransactionID,
		"amount":         req.Amount,
		"provider":       req.Provider,
		"reason":         req.Reason,
		"status":         resp.Status,
		"timestamp":      time.Now(),
	}

	if err := s.producer.PublishWithKey(ctx, kafka.TopicPaymentRefunded, resp.RefundID, kafka.EventPaymentRefunded, event); err != nil {
		logger.Error("failed to publish refund event", zap.Error(err))
	}
}

func (h *Handler) paymentMethods(w http.ResponseWriter, r *http.Request) {
	switch r.Method {
	case http.MethodGet:
		userID := r.URL.Query().Get("user_id")
		provider := r.URL.Query().Get("provider")

		if userID == "" {
			response.Error(w, &badRequestError{message: "user_id is required"})
			return
		}

		if provider == "" {
			provider = model.ProviderStripe
		}

		methods, err := h.gatewayService.GetPaymentMethods(r.Context(), userID, provider)
		if err != nil {
			response.Error(w, err)
			return
		}
		response.OK(w, methods)

	case http.MethodPost:
		var req model.CreatePaymentMethodRequest
		if err := json.NewDecoder(r.Body).Decode(&req); err != nil {
			response.Error(w, &badRequestError{message: "invalid request body"})
			return
		}

		if req.Provider == "" {
			req.Provider = model.ProviderStripe
		}

		pm, err := h.gatewayService.CreatePaymentMethod(r.Context(), &req)
		if err != nil {
			response.Error(w, err)
			return
		}
		response.Created(w, pm)

	default:
		response.Error(w, &methodNotAllowedError{})
	}
}

func (s *GatewayService) GetPaymentMethods(ctx context.Context, userID string, provider string) ([]*model.PaymentMethod, error) {
	// Validate provider
	client, ok := s.providerClients[provider]
	if !ok {
		return nil, errors.ValidationError(fmt.Sprintf("unsupported payment provider: %s", provider))
	}

	// Forward request to provider
	resp, err := client.Get(ctx, fmt.Sprintf("/api/v1/payment-methods?user_id=%s", userID), nil)
	if err != nil {
		return nil, errors.Wrap(err, errors.CodeInternalServerError, "failed to get payment methods", 500)
	}

	var methods []*model.PaymentMethod
	if err := resp.DecodeJSON(&methods); err != nil {
		return nil, errors.Wrap(err, errors.CodeInternalServerError, "failed to decode payment methods", 500)
	}

	return methods, nil
}

func (s *GatewayService) CreatePaymentMethod(ctx context.Context, req *model.CreatePaymentMethodRequest) (*model.PaymentMethod, error) {
	// Validate provider
	client, ok := s.providerClients[req.Provider]
	if !ok {
		return nil, errors.ValidationError(fmt.Sprintf("unsupported payment provider: %s", req.Provider))
	}

	// Forward request to provider
	resp, err := client.Post(ctx, "/api/v1/payment-methods", req, nil)
	if err != nil {
		return nil, errors.Wrap(err, errors.CodeInternalServerError, "failed to create payment method", 500)
	}

	if !resp.IsSuccess() {
		return nil, errors.InternalServerError("failed to create payment method with provider")
	}

	var pm model.PaymentMethod
	if err := resp.DecodeJSON(&pm); err != nil {
		return nil, errors.Wrap(err, errors.CodeInternalServerError, "failed to decode payment method response", 500)
	}

	return &pm, nil
}

func (h *Handler) listProviders(w http.ResponseWriter, r *http.Request) {
	if r.Method != http.MethodGet {
		response.Error(w, &methodNotAllowedError{})
		return
	}

	providers := h.gatewayService.GetSupportedProviders()
	response.OK(w, map[string]interface{}{
		"providers": providers,
	})
}

func (s *GatewayService) GetSupportedProviders() []string {
	return []string{
		model.ProviderStripe,
		model.ProviderBraintree,
		model.ProviderWorldpay,
	}
}

func (h *Handler) providerHealth(w http.ResponseWriter, r *http.Request) {
	if r.Method != http.MethodGet {
		response.Error(w, &methodNotAllowedError{})
		return
	}

	health := h.gatewayService.CheckProviderHealth(r.Context())
	response.OK(w, map[string]interface{}{
		"providers": health,
	})
}

func (s *GatewayService) CheckProviderHealth(ctx context.Context) []*model.ProviderHealthResponse {
	var results []*model.ProviderHealthResponse

	for provider, client := range s.providerClients {
		start := time.Now()
		resp, err := client.Get(ctx, "/health", nil)
		latency := time.Since(start).Milliseconds()

		result := &model.ProviderHealthResponse{
			Provider:  provider,
			Available: err == nil && resp.IsSuccess(),
			Latency:   latency,
		}
		results = append(results, result)
	}

	return results
}
