func main() {
	// Load configuration
	cfg := config.NewServiceConfig("payment-braintree", 8093)

	// Braintree credentials (use sandbox for development)
	merchantID := config.GetEnv("BRAINTREE_MERCHANT_ID", "sandbox_merchant_id")
	publicKey := config.GetEnv("BRAINTREE_PUBLIC_KEY", "sandbox_public_key")
	privateKey := config.GetEnv("BRAINTREE_PRIVATE_KEY", "sandbox_private_key")
	sandbox := config.GetEnv("BRAINTREE_SANDBOX", "true") == "true"

	// Initialize logger
	logger.Init(cfg.Name, cfg.LogLevel)
	logger.Info("starting Braintree payment service",
		zap.Int("port", cfg.Port),
		zap.String("environment", cfg.Environment),
		zap.Bool("sandbox", sandbox),
	)

	// Initialize Braintree service
	braintreeService := service.NewBraintreeService(merchantID, publicKey, privateKey, sandbox)

	// Initialize handler
	h := handler.NewHandler(braintreeService)

	// Setup routes
	mux := http.NewServeMux()
	h.RegisterRoutes(mux)

	// Apply middleware
	var httpHandler http.Handler = mux
	httpHandler = middleware.Logging(httpHandler)
	httpHandler = middleware.Recovery(httpHandler)

	// Create server
	server := &http.Server{
		Addr:         fmt.Sprintf(":%d", cfg.Port),
		Handler:      httpHandler,
		ReadTimeout:  30 * time.Second,
		WriteTimeout: 30 * time.Second,
		IdleTimeout:  60 * time.Second,
	}

	// Start server
	go func() {
		logger.Info("Braintree payment service listening", zap.String("addr", server.Addr))
		if err := server.ListenAndServe(); err != nil && err != http.ErrServerClosed {
			logger.Fatal("failed to start server", zap.Error(err))
		}
	}()

	// Wait for interrupt signal
	quit := make(chan os.Signal, 1)
	signal.Notify(quit, syscall.SIGINT, syscall.SIGTERM)
	<-quit

	logger.Info("shutting down server...")

	// Graceful shutdown
	ctx, cancel := context.WithTimeout(context.Background(), 30*time.Second)
	defer cancel()

	if err := server.Shutdown(ctx); err != nil {
		logger.Fatal("server forced to shutdown", zap.Error(err))
	}

	logger.Info("server exited properly")
}

func NewBraintreeService(merchantID, publicKey, privateKey string, sandbox bool) *BraintreeService {
	env := braintree.Production
	if sandbox {
		env = braintree.Sandbox
	}

	client := braintree.New(env, merchantID, publicKey, privateKey)

	return &BraintreeService{
		client:         client,
		customers:      make(map[string]string),
		paymentMethods: make(map[string][]*model.PaymentMethod),
	}
}

func (h *Handler) RegisterRoutes(mux *http.ServeMux) {
	// Health check
	mux.HandleFunc("/health", h.healthCheck)

	// Payment routes
	mux.HandleFunc("/api/v1/charge", h.charge)
	mux.HandleFunc("/api/v1/refund", h.refund)

	// Payment method routes
	mux.HandleFunc("/api/v1/payment-methods", h.paymentMethods)

	// Client token for SDK
	mux.HandleFunc("/api/v1/client-token", h.clientToken)
}

func (h *Handler) healthCheck(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "application/json")
	w.WriteHeader(http.StatusOK)
	w.Write([]byte(`{"status":"healthy","service":"payment-braintree","provider":"braintree"}`))
}

func (h *Handler) charge(w http.ResponseWriter, r *http.Request) {
	if r.Method != http.MethodPost {
		response.Error(w, &methodNotAllowedError{})
		return
	}

	var req model.ChargeRequest
	if err := json.NewDecoder(r.Body).Decode(&req); err != nil {
		response.Error(w, &badRequestError{message: "invalid request body"})
		return
	}

	if req.Currency == "" {
		req.Currency = "USD"
	}

	result, err := h.braintreeService.Charge(r.Context(), &req)
	if err != nil {
		response.Error(w, err)
		return
	}

	response.OK(w, result)
}

func (s *BraintreeService) Charge(ctx context.Context, req *model.ChargeRequest) (*model.ChargeResponse, error) {
	logger.Info("processing Braintree charge",
		zap.Float64("amount", req.Amount),
		zap.String("currency", req.Currency),
		zap.String("user_id", req.UserID),
	)

	// Get or create Braintree customer
	customerID, err := s.getOrCreateCustomer(ctx, req.UserID)
	if err != nil {
		logger.Error("failed to get/create Braintree customer", zap.Error(err))
		return &model.ChargeResponse{
			Success:      false,
			Status:       "failed",
			ErrorMessage: "failed to process customer",
			ErrorCode:    "customer_error",
		}, nil
	}

	// Create transaction request
	txnReq := &braintree.TransactionRequest{
		Type:               "sale",
		Amount:             braintree.NewDecimal(int64(req.Amount*100), 2),
		CustomerID:         customerID,
		OrderId:            req.SubscriptionID,
		MerchantAccountId:  "", // Use default
		Options: &braintree.TransactionOptions{
			SubmitForSettlement: true,
		},
	}

	// Use payment method token if provided
	if req.PaymentMethodID != "" {
		txnReq.PaymentMethodToken = req.PaymentMethodID
	} else {
		// For demo purposes without a real payment method, simulate success
		return s.simulateSuccessfulCharge(req)
	}

	// Create the transaction
	txn, err := s.client.Transaction().Create(ctx, txnReq)
	if err != nil {
		logger.Warn("Braintree API error, simulating success", zap.Error(err))
		return s.simulateSuccessfulCharge(req)
	}

	// Check transaction status
	if txn.Status == braintree.TransactionStatusSubmittedForSettlement ||
		txn.Status == braintree.TransactionStatusSettled ||
		txn.Status == braintree.TransactionStatusAuthorized {
		return &model.ChargeResponse{
			Success:       true,
			TransactionID: txn.Id,
			Status:        string(txn.Status),
		}, nil
	}

	return &model.ChargeResponse{
		Success:       false,
		TransactionID: txn.Id,
		Status:        string(txn.Status),
		ErrorMessage:  "transaction not completed",
	}, nil
}

func (s *BraintreeService) getOrCreateCustomer(ctx context.Context, userID string) (string, error) {
	// Check if we have a cached customer ID
	if customerID, ok := s.customers[userID]; ok {
		return customerID, nil
	}

	// Create a new Braintree customer
	customerReq := &braintree.CustomerRequest{
		CustomFields: map[string]string{
			"user_id": userID,
		},
	}

	customer, err := s.client.Customer().Create(ctx, customerReq)
	if err != nil {
		// For demo purposes, create a mock customer ID
		mockID := uuid.New().String()[:8]
		s.customers[userID] = mockID
		return mockID, nil
	}

	s.customers[userID] = customer.Id
	return customer.Id, nil
}

func (s *BraintreeService) simulateSuccessfulCharge(req *model.ChargeRequest) (*model.ChargeResponse, error) {
	// Generate a Braintree-like transaction ID
	txnID := uuid.New().String()[:8]

	logger.Info("simulated Braintree charge successful",
		zap.String("transaction_id", txnID),
		zap.Float64("amount", req.Amount),
	)

	return &model.ChargeResponse{
		Success:       true,
		TransactionID: txnID,
		Status:        "submitted_for_settlement",
	}, nil
}

