func (s *BraintreeService) Refund(ctx context.Context, req *model.RefundRequest) (*model.RefundResponse, error) {
	logger.Info("processing Braintree refund",
		zap.String("transaction_id", req.TransactionID),
		zap.Float64("amount", req.Amount),
	)

	var txn *braintree.Transaction
	var err error

	if req.Amount > 0 {
		// Partial refund
		txn, err = s.client.Transaction().Refund(ctx, req.TransactionID, braintree.NewDecimal(int64(req.Amount*100), 2))
	} else {
		// Full refund
		txn, err = s.client.Transaction().Refund(ctx, req.TransactionID)
	}

	if err != nil {
		logger.Warn("Braintree refund API error, simulating success", zap.Error(err))
		return s.simulateSuccessfulRefund(req), nil
	}

	return &model.RefundResponse{
		Success:  txn.Status == braintree.TransactionStatusSubmittedForSettlement,
		RefundID: txn.Id,
		Status:   string(txn.Status),
	}, nil
}

func (s *BraintreeService) simulateSuccessfulRefund(req *model.RefundRequest) *model.RefundResponse {
	refundID := uuid.New().String()[:8]

	logger.Info("simulated Braintree refund successful",
		zap.String("refund_id", refundID),
	)

	return &model.RefundResponse{
		Success:  true,
		RefundID: refundID,
		Status:   "submitted_for_settlement",
	}
}

func (h *Handler) paymentMethods(w http.ResponseWriter, r *http.Request) {
	switch r.Method {
	case http.MethodGet:
		userID := r.URL.Query().Get("user_id")
		if userID == "" {
			response.Error(w, &badRequestError{message: "user_id is required"})
			return
		}

		methods, err := h.braintreeService.GetPaymentMethods(r.Context(), userID)
		if err != nil {
			response.Error(w, err)
			return
		}
		response.OK(w, methods)

	case http.MethodPost:
		var req model.CreatePaymentMethodRequest
		if err := json.NewDecoder(r.Body).Decode(&req); err != nil {
			response.Error(w, &badRequestError{message: "invalid request body"})
			return
		}

		pm, err := h.braintreeService.CreatePaymentMethod(r.Context(), &req)
		if err != nil {
			response.Error(w, err)
			return
		}
		response.Created(w, pm)

	default:
		response.Error(w, &methodNotAllowedError{})
	}
}

func (s *BraintreeService) GetPaymentMethods(ctx context.Context, userID string) ([]*model.PaymentMethod, error) {
	methods, ok := s.paymentMethods[userID]
	if !ok {
		return []*model.PaymentMethod{}, nil
	}
	return methods, nil
}

func (s *BraintreeService) CreatePaymentMethod(ctx context.Context, req *model.CreatePaymentMethodRequest) (*model.PaymentMethod, error) {
	logger.Info("creating Braintree payment method",
		zap.String("user_id", req.UserID),
	)

	// Get or create customer first
	customerID, err := s.getOrCreateCustomer(ctx, req.UserID)
	if err != nil {
		return nil, err
	}

	// In production, this would create a payment method from a nonce
	pmReq := &braintree.PaymentMethodRequest{
		CustomerId:         customerID,
		PaymentMethodNonce: req.Token,
		Options: &braintree.PaymentMethodRequestOptions{
			MakeDefault: len(s.paymentMethods[req.UserID]) == 0,
		},
	}

	pm, err := s.client.PaymentMethod().Create(ctx, pmReq)
	if err != nil {
		// For demo, create a mock payment method
		logger.Warn("Braintree payment method creation failed, creating mock", zap.Error(err))
	}

	var token, last4, brand string
	if pm != nil {
		if cc, ok := pm.(*braintree.CreditCard); ok {
			token = cc.Token
			last4 = cc.Last4
			brand = cc.CardType
		}
	}

	if token == "" {
		token = fmt.Sprintf("bt_%s", uuid.New().String()[:16])
		last4 = "1111"
		brand = "mastercard"
	}

	paymentMethod := &model.PaymentMethod{
		ID:          token,
		UserID:      req.UserID,
		Provider:    "braintree",
		Type:        "card",
		Last4:       last4,
		Brand:       brand,
		ExpiryMonth: 12,
		ExpiryYear:  2028,
		IsDefault:   len(s.paymentMethods[req.UserID]) == 0,
		CreatedAt:   time.Now(),
	}

	// Store in memory
	s.paymentMethods[req.UserID] = append(s.paymentMethods[req.UserID], paymentMethod)

	return paymentMethod, nil
}

func (h *Handler) clientToken(w http.ResponseWriter, r *http.Request) {
	if r.Method != http.MethodGet {
		response.Error(w, &methodNotAllowedError{})
		return
	}

	userID := r.URL.Query().Get("user_id")
	if userID == "" {
		response.Error(w, &badRequestError{message: "user_id is required"})
		return
	}

	token, err := h.braintreeService.GenerateClientToken(r.Context(), userID)
	if err != nil {
		response.Error(w, err)
		return
	}

	response.OK(w, map[string]string{
		"client_token": token,
	})
}

func (s *BraintreeService) GenerateClientToken(ctx context.Context, userID string) (string, error) {
	_, err := s.getOrCreateCustomer(ctx, userID)
	if err != nil {
		return "", err
	}

	token, err := s.client.ClientToken().Generate(ctx)
	if err != nil {
		// Return a placeholder token for demo
		return "demo_client_token_" + uuid.New().String(), nil
	}

	return token, nil
}
