func main() {
	// Load configuration
	cfg := config.NewServiceConfig("payment-stripe", 8092)

	// Stripe API key (use test key for development)
	stripeAPIKey := config.GetEnv("STRIPE_API_KEY", "sk_test_placeholder")

	// Initialize logger
	logger.Init(cfg.Name, cfg.LogLevel)
	logger.Info("starting Stripe payment service",
		zap.Int("port", cfg.Port),
		zap.String("environment", cfg.Environment),
	)

	// Initialize Stripe service
	stripeService := service.NewStripeService(stripeAPIKey)

	// Initialize handler
	h := handler.NewHandler(stripeService)

	// Setup routes
	mux := http.NewServeMux()
	h.RegisterRoutes(mux)

	// Apply middleware
	var httpHandler http.Handler = mux
	httpHandler = middleware.Logging(httpHandler)
	httpHandler = middleware.Recovery(httpHandler)

	// Create server
	server := &http.Server{
		Addr:         fmt.Sprintf(":%d", cfg.Port),
		Handler:      httpHandler,
		ReadTimeout:  30 * time.Second,
		WriteTimeout: 30 * time.Second,
		IdleTimeout:  60 * time.Second,
	}

	// Start server
	go func() {
		logger.Info("Stripe payment service listening", zap.String("addr", server.Addr))
		if err := server.ListenAndServe(); err != nil && err != http.ErrServerClosed {
			logger.Fatal("failed to start server", zap.Error(err))
		}
	}()

	// Wait for interrupt signal
	quit := make(chan os.Signal, 1)
	signal.Notify(quit, syscall.SIGINT, syscall.SIGTERM)
	<-quit

	logger.Info("shutting down server...")

	// Graceful shutdown
	ctx, cancel := context.WithTimeout(context.Background(), 30*time.Second)
	defer cancel()

	if err := server.Shutdown(ctx); err != nil {
		logger.Fatal("server forced to shutdown", zap.Error(err))
	}

	logger.Info("server exited properly")
}

func NewStripeService(apiKey string) *StripeService {
	// Set the Stripe API key globally
	stripe.Key = apiKey

	return &StripeService{
		apiKey:         apiKey,
		customers:      make(map[string]string),
		paymentMethods: make(map[string][]*model.PaymentMethod),
	}
}

func (h *Handler) RegisterRoutes(mux *http.ServeMux) {
	// Health check
	mux.HandleFunc("/health", h.healthCheck)

	// Payment routes
	mux.HandleFunc("/api/v1/charge", h.charge)
	mux.HandleFunc("/api/v1/refund", h.refund)

	// Payment method routes
	mux.HandleFunc("/api/v1/payment-methods", h.paymentMethods)
}

func (h *Handler) healthCheck(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "application/json")
	w.WriteHeader(http.StatusOK)
	w.Write([]byte(`{"status":"healthy","service":"payment-stripe","provider":"stripe"}`))
}

func (h *Handler) charge(w http.ResponseWriter, r *http.Request) {
	if r.Method != http.MethodPost {
		response.Error(w, &methodNotAllowedError{})
		return
	}

	var req model.ChargeRequest
	if err := json.NewDecoder(r.Body).Decode(&req); err != nil {
		response.Error(w, &badRequestError{message: "invalid request body"})
		return
	}

	if req.Currency == "" {
		req.Currency = "usd"
	}

	result, err := h.stripeService.Charge(r.Context(), &req)
	if err != nil {
		response.Error(w, err)
		return
	}

	response.OK(w, result)
}

func (s *StripeService) Charge(ctx context.Context, req *model.ChargeRequest) (*model.ChargeResponse, error) {
	logger.Info("processing Stripe charge",
		zap.Float64("amount", req.Amount),
		zap.String("currency", req.Currency),
		zap.String("user_id", req.UserID),
	)

	// Convert amount to cents
	amountCents := int64(req.Amount * 100)

	// Get or create Stripe customer
	customerID, err := s.getOrCreateCustomer(ctx, req.UserID)
	if err != nil {
		logger.Error("failed to get/create Stripe customer", zap.Error(err))
		return &model.ChargeResponse{
			Success:      false,
			Status:       "failed",
			ErrorMessage: "failed to process customer",
			ErrorCode:    "customer_error",
		}, nil
	}

	// Create PaymentIntent (Stripe's recommended approach)
	params := &stripe.PaymentIntentParams{
		Amount:   stripe.Int64(amountCents),
		Currency: stripe.String(req.Currency),
		Customer: stripe.String(customerID),
		Metadata: make(map[string]string),
	}

	// Add metadata
	if req.SubscriptionID != "" {
		params.Metadata["subscription_id"] = req.SubscriptionID
	}
	params.Metadata["user_id"] = req.UserID

	// Add description
	if req.Description != "" {
		params.Description = stripe.String(req.Description)
	}

	// If payment method specified, attach it
	if req.PaymentMethodID != "" {
		params.PaymentMethod = stripe.String(req.PaymentMethodID)
		params.Confirm = stripe.Bool(true)
		params.OffSession = stripe.Bool(true)
	} else {
		// For demo purposes without a real payment method, simulate success
		return s.simulateSuccessfulCharge(req)
	}

	// Create the payment intent
	pi, err := paymentintent.New(params)
	if err != nil {
		if stripeErr, ok := err.(*stripe.Error); ok {
			logger.Warn("Stripe API error",
				zap.String("code", string(stripeErr.Code)),
				zap.String("message", stripeErr.Msg),
			)
			return &model.ChargeResponse{
				Success:      false,
				Status:       "failed",
				ErrorMessage: stripeErr.Msg,
				ErrorCode:    string(stripeErr.Code),
			}, nil
		}
		return &model.ChargeResponse{
			Success:      false,
			Status:       "failed",
			ErrorMessage: err.Error(),
		}, nil
	}

	// Check payment intent status
	if pi.Status == stripe.PaymentIntentStatusSucceeded {
		return &model.ChargeResponse{
			Success:       true,
			TransactionID: pi.ID,
			Status:        "succeeded",
		}, nil
	}

	return &model.ChargeResponse{
		Success:       false,
		TransactionID: pi.ID,
		Status:        string(pi.Status),
		ErrorMessage:  "payment not completed",
	}, nil
}

func (s *StripeService) getOrCreateCustomer(ctx context.Context, userID string) (string, error) {
	// Check if we have a cached customer ID
	if customerID, ok := s.customers[userID]; ok {
		return customerID, nil
	}

	// Create a new Stripe customer
	params := &stripe.CustomerParams{
		Metadata: map[string]string{
			"user_id": userID,
		},
	}

	c, err := customer.New(params)
	if err != nil {
		// For demo purposes, create a mock customer ID
		mockID := fmt.Sprintf("cus_%s", uuid.New().String()[:24])
		s.customers[userID] = mockID
		return mockID, nil
	}

	s.customers[userID] = c.ID
	return c.ID, nil
}

func (s *StripeService) simulateSuccessfulCharge(req *model.ChargeRequest) (*model.ChargeResponse, error) {
	// Generate a fake Stripe-like transaction ID
	txnID := fmt.Sprintf("pi_%s", uuid.New().String()[:24])

	logger.Info("simulated Stripe charge successful",
		zap.String("transaction_id", txnID),
		zap.Float64("amount", req.Amount),
	)

	return &model.ChargeResponse{
		Success:       true,
		TransactionID: txnID,
		Status:        "succeeded",
	}, nil
}

func (h *Handler) refund(w http.ResponseWriter, r *http.Request) {
	if r.Method != http.MethodPost {
		response.Error(w, &methodNotAllowedError{})
		return
	}

	var req model.RefundRequest
	if err := json.NewDecoder(r.Body).Decode(&req); err != nil {
		response.Error(w, &badRequestError{message: "invalid request body"})
		return
	}

	result, err := h.stripeService.Refund(r.Context(), &req)
