func (s *StripeService) Refund(ctx context.Context, req *model.RefundRequest) (*model.RefundResponse, error) {
	logger.Info("processing Stripe refund",
		zap.String("transaction_id", req.TransactionID),
		zap.Float64("amount", req.Amount),
	)

	params := &stripe.RefundParams{
		PaymentIntent: stripe.String(req.TransactionID),
	}

	// Partial refund if amount specified
	if req.Amount > 0 {
		params.Amount = stripe.Int64(int64(req.Amount * 100))
	}

	if req.Reason != "" {
		switch req.Reason {
		case "duplicate":
			params.Reason = stripe.String(string(stripe.RefundReasonDuplicate))
		case "fraudulent":
			params.Reason = stripe.String(string(stripe.RefundReasonFraudulent))
		default:
			params.Reason = stripe.String(string(stripe.RefundReasonRequestedByCustomer))
		}
	}

	r, err := refund.New(params)
	if err != nil {
		// For demo purposes, simulate successful refund
		if stripeErr, ok := err.(*stripe.Error); ok {
			logger.Warn("Stripe refund API error, simulating success",
				zap.String("code", string(stripeErr.Code)),
			)
		}
		return s.simulateSuccessfulRefund(req), nil
	}

	return &model.RefundResponse{
		Success:  r.Status == stripe.RefundStatusSucceeded,
		RefundID: r.ID,
		Status:   string(r.Status),
	}, nil
}

func (s *StripeService) simulateSuccessfulRefund(req *model.RefundRequest) *model.RefundResponse {
	refundID := fmt.Sprintf("re_%s", uuid.New().String()[:24])

	logger.Info("simulated Stripe refund successful",
		zap.String("refund_id", refundID),
	)

	return &model.RefundResponse{
		Success:  true,
		RefundID: refundID,
		Status:   "succeeded",
	}
}

func (h *Handler) paymentMethods(w http.ResponseWriter, r *http.Request) {
	switch r.Method {
	case http.MethodGet:
		userID := r.URL.Query().Get("user_id")
		if userID == "" {
			response.Error(w, &badRequestError{message: "user_id is required"})
			return
		}

		methods, err := h.stripeService.GetPaymentMethods(r.Context(), userID)
		if err != nil {
			response.Error(w, err)
			return
		}
		response.OK(w, methods)

	case http.MethodPost:
		var req model.CreatePaymentMethodRequest
		if err := json.NewDecoder(r.Body).Decode(&req); err != nil {
			response.Error(w, &badRequestError{message: "invalid request body"})
			return
		}

		pm, err := h.stripeService.CreatePaymentMethod(r.Context(), &req)
		if err != nil {
			response.Error(w, err)
			return
		}
		response.Created(w, pm)

	default:
		response.Error(w, &methodNotAllowedError{})
	}
}

func (s *StripeService) GetPaymentMethods(ctx context.Context, userID string) ([]*model.PaymentMethod, error) {
	methods, ok := s.paymentMethods[userID]
	if !ok {
		return []*model.PaymentMethod{}, nil
	}
	return methods, nil
}

func (s *StripeService) CreatePaymentMethod(ctx context.Context, req *model.CreatePaymentMethodRequest) (*model.PaymentMethod, error) {
	logger.Info("creating Stripe payment method",
		zap.String("user_id", req.UserID),
	)

	// In production, this would attach a tokenized payment method to a customer
	// For demo purposes, we'll create a mock payment method

	pm := &model.PaymentMethod{
		ID:          fmt.Sprintf("pm_%s", uuid.New().String()[:24]),
		UserID:      req.UserID,
		Provider:    "stripe",
		Type:        "card",
		Last4:       "4242",
		Brand:       "visa",
		ExpiryMonth: 12,
		ExpiryYear:  2027,
		IsDefault:   len(s.paymentMethods[req.UserID]) == 0,
		CreatedAt:   time.Now(),
	}

	// Store in memory
	s.paymentMethods[req.UserID] = append(s.paymentMethods[req.UserID], pm)

	return pm, nil
}
