func main() {
	// Load configuration
	cfg := config.NewServiceConfig("gateway", 8080)
	jwtCfg := config.NewJWTConfig()

	// Initialize logger
	logger.Init(cfg.Name, cfg.LogLevel)
	logger.Info("starting gateway service",
		zap.Int("port", cfg.Port),
		zap.String("environment", cfg.Environment),
	)

	// Initialize JWT auth
	jwtAuth := middleware.NewJWTAuth(jwtCfg.Secret)

	// Initialize service proxies
	userServiceURL := config.GetEnv("USER_SERVICE_URL", "http://localhost:8081")
	profileServiceURL := config.GetEnv("PROFILE_SERVICE_URL", "http://localhost:8082")
	matchingServiceURL := config.GetEnv("MATCHING_SERVICE_URL", "http://localhost:8083")
	notificationServiceURL := config.GetEnv("NOTIFICATION_SERVICE_URL", "http://localhost:8084")
	mediaServiceURL := config.GetEnv("MEDIA_SERVICE_URL", "http://localhost:8086")
	analyticsServiceURL := config.GetEnv("ANALYTICS_SERVICE_URL", "http://localhost:8087")

	proxyManager := proxy.NewManager(map[string]string{
		"user":         userServiceURL,
		"profile":      profileServiceURL,
		"matching":     matchingServiceURL,
		"notification": notificationServiceURL,
		"media":        mediaServiceURL,
		"analytics":    analyticsServiceURL,
	})

	// Initialize handler
	h := handler.NewHandler(proxyManager, jwtAuth)

	// Setup routes
	mux := http.NewServeMux()
	h.RegisterRoutes(mux)

	// Apply middleware
	var handler http.Handler = mux
	handler = middleware.Logging(handler)
	handler = middleware.Recovery(handler)
	handler = middleware.CORS(handler)

	// Create server
	server := &http.Server{
		Addr:         fmt.Sprintf(":%d", cfg.Port),
		Handler:      handler,
		ReadTimeout:  15 * time.Second,
		WriteTimeout: 15 * time.Second,
		IdleTimeout:  60 * time.Second,
	}

	// Start server
	go func() {
		logger.Info("gateway listening", zap.String("addr", server.Addr))
		if err := server.ListenAndServe(); err != nil && err != http.ErrServerClosed {
			logger.Fatal("failed to start server", zap.Error(err))
		}
	}()

	// Wait for interrupt signal
	quit := make(chan os.Signal, 1)
	signal.Notify(quit, syscall.SIGINT, syscall.SIGTERM)
	<-quit

	logger.Info("shutting down server...")

	// Graceful shutdown
	ctx, cancel := context.WithTimeout(context.Background(), 30*time.Second)
	defer cancel()

	if err := server.Shutdown(ctx); err != nil {
		logger.Fatal("server forced to shutdown", zap.Error(err))
	}

	logger.Info("server exited properly")
}

func NewManager(serviceURLs map[string]string) *Manager {
	manager := &Manager{
		services: make(map[string]*httputil.ReverseProxy),
		urls:     serviceURLs,
	}

	for name, urlStr := range serviceURLs {
		targetURL, err := url.Parse(urlStr)
		if err != nil {
			logger.Error("failed to parse service URL",
				zap.String("service", name),
				zap.String("url", urlStr),
				zap.Error(err),
			)
			continue
		}

		proxy := httputil.NewSingleHostReverseProxy(targetURL)
		proxy.Transport = &http.Transport{
			MaxIdleConns:        100,
			MaxIdleConnsPerHost: 100,
			IdleConnTimeout:     90 * time.Second,
		}

		// Custom error handler
		proxy.ErrorHandler = func(w http.ResponseWriter, r *http.Request, err error) {
			logger.Error("proxy error",
				zap.String("service", name),
				zap.String("path", r.URL.Path),
				zap.Error(err),
			)
			w.Header().Set("Content-Type", "application/json")
			w.WriteHeader(http.StatusBadGateway)
			w.Write([]byte(`{"success":false,"error":{"code":"SERVICE_UNAVAILABLE","message":"Service temporarily unavailable"}}`))
		}

		// Custom director to modify the request
		originalDirector := proxy.Director
		proxy.Director = func(req *http.Request) {
			originalDirector(req)
			req.Host = targetURL.Host
		}

		// Modify response to prevent duplicate CORS headers
		proxy.ModifyResponse = func(resp *http.Response) error {
			// Remove any CORS headers set by backend service
			// (gateway middleware will add them)
			resp.Header.Del("Access-Control-Allow-Origin")
			resp.Header.Del("Access-Control-Allow-Methods")
			resp.Header.Del("Access-Control-Allow-Headers")
			resp.Header.Del("Access-Control-Allow-Credentials")
			resp.Header.Del("Access-Control-Max-Age")
			return nil
		}

		manager.services[name] = proxy
		logger.Info("registered service proxy",
			zap.String("service", name),
			zap.String("url", urlStr),
		)
	}

	return manager
}

func NewHandler(proxyManager *proxy.Manager, jwtAuth *middleware.JWTAuth) *Handler {
	return &Handler{
		proxyManager: proxyManager,
		jwtAuth:      jwtAuth,
	}
}

func (h *Handler) RegisterRoutes(mux *http.ServeMux) {
	// Health check
	mux.HandleFunc("/health", h.healthCheck)
	mux.Handle("/health/services", h.proxyManager.HealthCheck())

	// API v1 routes
	mux.HandleFunc("/api/v1/", h.routeAPI)
}

func (h *Handler) healthCheck(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "application/json")
	w.WriteHeader(http.StatusOK)
	w.Write([]byte(`{"status":"healthy","service":"gateway"}`))
}

func (m *Manager) HealthCheck() http.Handler {
	return http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
		results := make(map[string]string)
		allHealthy := true

		for name, urlStr := range m.urls {
			healthURL := urlStr + "/health"
			resp, err := http.Get(healthURL)
			if err != nil || resp.StatusCode != http.StatusOK {
				results[name] = "unhealthy"
				allHealthy = false
			} else {
				results[name] = "healthy"
			}
			if resp != nil {
				io.Copy(io.Discard, resp.Body)
				resp.Body.Close()
			}
		}

		w.Header().Set("Content-Type", "application/json")
		if allHealthy {
			w.WriteHeader(http.StatusOK)
		} else {
			w.WriteHeader(http.StatusServiceUnavailable)
		}

		// Build response
		w.Write([]byte(`{"gateway":"healthy","services":{`))
		first := true
		for name, status := range results {
			if !first {
				w.Write([]byte(","))
			}
			w.Write([]byte(`"` + name + `":"` + status + `"`))
			first = false
		}
		w.Write([]byte(`}}`))
	})
}

