func (h *Handler) routeAPI(w http.ResponseWriter, r *http.Request) {
	path := strings.TrimPrefix(r.URL.Path, "/api/v1")

	// Route based on path prefix
	switch {
	// Auth routes (public)
	case strings.HasPrefix(path, "/auth"):
		h.proxyToService(w, r, "user")

	// User routes (authenticated)
	case strings.HasPrefix(path, "/users"):
		h.authenticatedProxy(w, r, "user")

	// Profile routes - GET is public, others require auth
	case strings.HasPrefix(path, "/profiles"):
		if r.Method == http.MethodGet && !strings.HasPrefix(path, "/profiles/me") {
			// Public read access for browsing profiles
			h.proxyToService(w, r, "profile")
		} else {
			// Create, Update, Delete, and /me require authentication
			h.authenticatedProxy(w, r, "profile")
		}

	// Matching routes (all authenticated)
	case strings.HasPrefix(path, "/swipes"):
		h.authenticatedProxy(w, r, "matching")
	case strings.HasPrefix(path, "/matches"):
		h.authenticatedProxy(w, r, "matching")

	// Notification routes (all authenticated)
	case strings.HasPrefix(path, "/notifications"):
		h.authenticatedProxy(w, r, "notification")

	// Media routes - GET profile media is public, uploads require auth
	case strings.HasPrefix(path, "/media"):
		if r.Method == http.MethodGet {
			// Public read access for viewing photos
			h.proxyToService(w, r, "media")
		} else {
			// Upload, Delete require authentication
			h.authenticatedProxy(w, r, "media")
		}

	// Analytics routes (public for demo, can be secured later)
	case strings.HasPrefix(path, "/analytics"):
		h.proxyToService(w, r, "analytics")

	default:
		w.Header().Set("Content-Type", "application/json")
		w.WriteHeader(http.StatusNotFound)
		w.Write([]byte(`{"success":false,"error":{"code":"NOT_FOUND","message":"Endpoint not found"}}`))
	}
}

func (h *Handler) proxyToService(w http.ResponseWriter, r *http.Request, service string) {
	// Proxy the full path as-is to the backend service
	handler := h.proxyManager.Proxy(service)
	handler.ServeHTTP(w, r)
}

func (m *Manager) Proxy(serviceName string) http.Handler {
	return http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
		proxy, exists := m.services[serviceName]
		if !exists {
			logger.Error("service not found", zap.String("service", serviceName))
			w.Header().Set("Content-Type", "application/json")
			w.WriteHeader(http.StatusNotFound)
			w.Write([]byte(`{"success":false,"error":{"code":"SERVICE_NOT_FOUND","message":"Service not found"}}`))
			return
		}

		logger.Debug("proxying request",
			zap.String("service", serviceName),
			zap.String("method", r.Method),
			zap.String("path", r.URL.Path),
		)

		proxy.ServeHTTP(w, r)
	})
}

