func (h *Handler) authenticatedProxy(w http.ResponseWriter, r *http.Request, service string) {
	// Validate JWT and add user info to headers
	authHeader := r.Header.Get("Authorization")
	if authHeader == "" {
		w.Header().Set("Content-Type", "application/json")
		w.WriteHeader(http.StatusUnauthorized)
		w.Write([]byte(`{"success":false,"error":{"code":"UNAUTHORIZED","message":"Missing authorization header"}}`))
		return
	}

	parts := strings.Split(authHeader, " ")
	if len(parts) != 2 || parts[0] != "Bearer" {
		w.Header().Set("Content-Type", "application/json")
		w.WriteHeader(http.StatusUnauthorized)
		w.Write([]byte(`{"success":false,"error":{"code":"UNAUTHORIZED","message":"Invalid authorization header format"}}`))
		return
	}

	claims, err := h.jwtAuth.ValidateToken(parts[1])
	if err != nil {
		w.Header().Set("Content-Type", "application/json")
		w.WriteHeader(http.StatusUnauthorized)
		w.Write([]byte(`{"success":false,"error":{"code":"UNAUTHORIZED","message":"Invalid or expired token"}}`))
		return
	}

	// Add user info to headers for downstream services
	r.Header.Set("X-User-ID", claims.UserID)
	r.Header.Set("X-User-Email", claims.Email)

	h.proxyToService(w, r, service)
}
